sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	g_form.setValue('u_string_candidate_name', 'aaaa');
   var ga = new GlideAjax('GetPRGEmployeeInformation');
   ga.addParam('sysparm_name', 'getEmp');
   ga.addParam('sysparm_userID', g_form.getValue('u_referance_candidate'));
   ga.getXML(updateCandidateInformation);
   function updateCandidateInformation(response)
	{
		g_form.clearValue('u_string_candidate_name');
		g_form.clearValue('u_integer_candidate_eid');
		g_form.clearValue('u_integer_candidate_companynum');
		g_form.clearValue('u_string_candidate_title');
		g_form.clearValue('u_integer_candidate_jobcode');
		g_form.clearValue('u_glide_date_candidate_hireddate');
		var candidateName = response.responseXML.documentElement.getAttribute("user_name");
		g_form.setValue('u_string_candidate_name', candidateName);
	}
   //Type appropriate comment here, and begin script below

}
